import{_ as e,c as r,o as l,ae as n}from"./chunks/framework.BM141d4E.js";const y=JSON.parse('{"title":"Συνέδριο ΕΓΕ 2025","description":"","frontmatter":{},"headers":[],"relativePath":"el/Extra/GSG Conference 2024.md","filePath":"el/Extra/GSG Conference 2024.md"}'),s={name:"el/Extra/GSG Conference 2024.md"};function a(d,t,o,i,g,f){return l(),r("div",null,t[0]||(t[0]=[n('<h1 id="συνεδριο-εγε-2025" tabindex="-1">Συνέδριο ΕΓΕ 2025 <a class="header-anchor" href="#συνεδριο-εγε-2025" aria-label="Permalink to &quot;Συνέδριο ΕΓΕ 2025&quot;">​</a></h1><p>Μετά από ΔΣ στις 20/01/2025 καταλήξαμε στις παρακάτω προτάσεις για το συνέδριο της Ελληνικής Γεωγραφικής Εταιρείας που θέτουμε υπόψη σας. Τα όσα συζητήθηκαν και στα οποία καταλήξαμε συνοψίζονται στον παρακάτω πίνακα και είμαστε στη διάθεσή σας για περαιτέρω διευκρινίσεις.</p><table tabindex="0"><thead><tr><th style="text-align:left;">Θέμα</th><th style="text-align:left;">Πρόταση</th></tr></thead><tbody><tr><td style="text-align:left;"><strong>Χρόνος διεξαγωγής συνεδρίου</strong></td><td style="text-align:left;">Διήμερο συνέδριο, Νοέμβριος 2025. 6-7-8 ή 20-21-22 (Σάββατο field trips) 1η ανακοίνωση αρχές Μαρτίου 2η ανακοίνωση μέσα Μαρτίου</td></tr><tr><td style="text-align:left;"><strong>Τόπος</strong></td><td style="text-align:left;">Χαροκόπειο Πανεπιστήμιο. Θα πρέπει μόλις αποφασιστεί η διεξαγωγή να έρθουμε σε επαφή με το Χαροκόπειο. Προετοιμασία επιστολής από τους Durie, Μαλούτα</td></tr><tr><td style="text-align:left;"><strong>Τίτλος</strong></td><td style="text-align:left;">Geography Matters</td></tr><tr><td style="text-align:left;"><strong>Οργανωτική Επιτροπή</strong></td><td style="text-align:left;">Προτείνουμε μια 10-μελή οργανωτική επιτροπή με σαφώς καθορισμένες αρμοδιότητες ανά άτομο. Ενδεικτικά παραθέτουμε τις θέσεις – αρμοδιότητες που θεωρούμε ότι θα πρέπει να μοιραστούν. Δεν έχουμε δώσει ακόμη το ορθό όνομα για την κάθε θέση-αρμοδιότητα. Θα γίνει αυτό αφότου το διοικητικό συμβούλιο συμφωνήσει με το σκεπτικό αυτό. Θα πρέπει να υπάρχουν μέλη από το Χαροκόπειο ώστε να απλοποιηθούν οι διαδικασίες οργάνωσης<br>Πρόεδρος: Από το Χαροκόπειο<br>Αντιπρόεδρος: Σαΐτης<br>Γραμματέας: (υπεύθυνος αλληλογραφίας) Καρκάνη + Σαΐτης<br>Ταμίας: Κλωνάρη<br>Υπεύθυνος για Abstracts (Οργάνωση/review): Μαλούτας, Πολύζου, Σπυρέλλης<br>Οργάνωση συνεδρίου (τόπος, αίθουσες, key note speakers): Μαλούτας, Durie, Σπυρέλλης<br>Οργάνωση Field trip: Ευελπίδου + Πηγάκη<br>Υπεύθυνος Dissemination: Durie + Σαΐτης<br>Υπεύθυνος Χορηγιών: Πηγάκη + Κουτσόπουλος<br>Υπεύθυνος Special Issue: Κουτσόπουλος<br>Υπεύθυνος για δράσεις όπως βραβεία κτλ.: όλοι</td></tr><tr><td style="text-align:left;"><strong>Επιστημονική Επιτροπή</strong></td><td style="text-align:left;">Αρκετά ανοιχτή επιστημονική επιτροπή από συναδέλφους που προσφέρουν ή έχουν προσφέρει στο χώρο και τιμητική παρουσία πολλών από αυτούς</td></tr><tr><td style="text-align:left;"><strong>Επαναληψιμότητα</strong></td><td style="text-align:left;">Κάθε δύο έτη</td></tr><tr><td style="text-align:left;"><strong>Διείσδυση</strong></td><td style="text-align:left;">Διεθνές συνέδριο. Να προσπαθήσουμε να συνδυάσουμε τα ευρωπαϊκά ή διεθνή έργα που τρέχουμε και τυχόν συναντήσεις τους με την περίοδο του συνεδρίου για να αυξήσουμε τις συμμετοχές από το εξωτερικό.</td></tr><tr><td style="text-align:left;"><strong>Δημοσίευση πρακτικών</strong></td><td style="text-align:left;">Πρακτικά συνεδρίου στο διαδίκτυο e-proceedings. Special issue στο European Journal of Geography. Θα αναλάβει κάποια ομάδα με συμμετοχή νέων επιστημόνων με την υποστήριξη του κ. Κουτσόπουλου.</td></tr><tr><td style="text-align:left;"><strong>Οικονομικά</strong></td><td style="text-align:left;">Τα fees θα πρέπει να είναι μικρό ποσό για να προσελκύσουμε κόσμο. Προτείνουμε: 60 ευρώ κανονικό fee / 40 για ενήμερα μέλη με 20 ευρώ για τους σπουδαστές / 10 για ενήμερους φοιτητές</td></tr><tr><td style="text-align:left;"><strong>Invited speaker και keynote speakers</strong></td><td style="text-align:left;">Το κόστος ίσως είναι απαγορευτικό ας δούμε τα πιθανά ονόματα. Καλό είναι να το δούμε αργότερα (τον Ιούνιο) ώστε να δούμε την συμμετοχή</td></tr><tr><td style="text-align:left;"><strong>Πραγματοποίηση Γ.Σ. μελών και εκλογή διοικητικού συμβουλίου</strong></td><td style="text-align:left;">Να προστεθεί στο πρόγραμμα</td></tr><tr><td style="text-align:left;"><strong>Συμμετοχή</strong></td><td style="text-align:left;">Ένα σαν κύριος ομιλητής και δύο συμμετοχές</td></tr></tbody></table>',3)]))}const p=e(s,[["render",a]]);export{y as __pageData,p as default};
