import{_ as t,c as r,o as n,ae as o}from"./chunks/framework.BM141d4E.js";const f=JSON.parse('{"title":"HGS Conference 2025","description":"","frontmatter":{},"headers":[],"relativePath":"en/Extra/GSG Conference 2024.md","filePath":"en/Extra/GSG Conference 2024.md"}'),i={name:"en/Extra/GSG Conference 2024.md"};function a(s,e,l,d,c,g){return n(),r("div",null,e[0]||(e[0]=[o('<h1 id="hgs-conference-2025" tabindex="-1">HGS Conference 2025 <a class="header-anchor" href="#hgs-conference-2025" aria-label="Permalink to &quot;HGS Conference 2025&quot;">​</a></h1><p>Following a Board meeting on 20/01/2025, we have reached the following proposals for the Hellenic Geographical Society conference, which we present for your consideration. The discussions and conclusions are summarized in the table below, and we are available for further clarification.</p><table tabindex="0"><thead><tr><th style="text-align:left;">Topic</th><th style="text-align:left;">Proposal</th></tr></thead><tbody><tr><td style="text-align:left;"><strong>Conference Dates</strong></td><td style="text-align:left;">Two-day conference, November 2025. Dates: 6-7-8 or 20-21-22 (Saturday for field trips). 1st announcement early March, 2nd announcement mid-March.</td></tr><tr><td style="text-align:left;"><strong>Venue</strong></td><td style="text-align:left;">Harokopio University. We need to contact Harokopio University as soon as the decision to proceed is made. Letter preparation by Durie, Maloutas.</td></tr><tr><td style="text-align:left;"><strong>Title</strong></td><td style="text-align:left;">Geography Matters</td></tr><tr><td style="text-align:left;"><strong>Organizing Committee</strong></td><td style="text-align:left;">We propose a 10-member organizing committee with clearly defined responsibilities per person. Indicative positions/responsibilities are listed below. The exact titles for each role will be decided after the board agrees on this framework. Members from Harokopio should be included to simplify organizational procedures.<br>Chair: From Harokopio University<br>Vice-Chair: Saitis<br>Secretary (correspondence): Karkani + Saitis<br>Treasurer: Klonari<br>Abstracts Lead (Organization/Review): Maloutas, Polyzou, Spyrellis<br>Conference Logistics (Venue, Rooms, Keynote Speakers): Maloutas, Durie, Spyrellis<br>Field Trip Organization: Evelpidou + Pigaki<br>Dissemination Lead: Durie + Saitis<br>Sponsorship Lead: Pigaki + Koutsopoulos<br>Special Issue Lead: Koutsopoulos<br>Awards/Other Activities Lead: All members</td></tr><tr><td style="text-align:left;"><strong>Scientific Committee</strong></td><td style="text-align:left;">A broadly composed scientific committee including colleagues who contribute or have contributed to the field, with many attending in an honorary capacity.</td></tr><tr><td style="text-align:left;"><strong>Frequency</strong></td><td style="text-align:left;">Every two years</td></tr><tr><td style="text-align:left;"><strong>Reach</strong></td><td style="text-align:left;">International conference. We should try to coordinate with ongoing European or international projects and any potential meetings they have during the conference period to increase international participation.</td></tr><tr><td style="text-align:left;"><strong>Publication of Proceedings</strong></td><td style="text-align:left;">Conference proceedings online (e-proceedings). Special issue in the European Journal of Geography. A team involving young scientists, supported by Mr. Koutsopoulos, will undertake this.</td></tr><tr><td style="text-align:left;"><strong>Finances</strong></td><td style="text-align:left;">Fees should be low to attract participants. We propose: €60 standard fee / €40 for members / €20 for students / €10 for student members.</td></tr><tr><td style="text-align:left;"><strong>Invited Speakers and Keynote Speakers</strong></td><td style="text-align:left;">The cost might be prohibitive; let&#39;s review potential names. It&#39;s better to decide later (in June) based on participation levels.</td></tr><tr><td style="text-align:left;"><strong>General Assembly Meeting &amp; Board Election</strong></td><td style="text-align:left;">To be added to the program.</td></tr><tr><td style="text-align:left;"><strong>Participation Limits (per person)</strong></td><td style="text-align:left;">One presentation as main speaker and two participations total.</td></tr></tbody></table>',3)]))}const y=t(i,[["render",a]]);export{f as __pageData,y as default};
